import {PutObjectCommand, S3Client} from "@aws-sdk/client-s3";
import { useState } from "react";
import { nanoid } from "nanoid";

function App() {

  const [file, setFile] = useState(null);
  const [inputText, setInputText] = useState('')

  const handleTextInputChange = (event) => {
      setInputText(event.target.value);
      };

const handleFileChange = (e) => {
  const file = e.target.files[0];
  setFile(file);
};

  const uploadFile = async () => {
    const S3_BUCKET = "infracdkstack-projects3bucketf7e4afb6-ljf45fw8yyuw";

    const REGION = "us-west-1";

      const CREDENTIALS = {
        accessKeyId: "AKIARFXZJQECRGPJWSW3",
        secretAccessKey: "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"
      }

      const s3Client = new S3Client({
        region: REGION,
        credentials: CREDENTIALS
    });

      console.log(file)
      const command = new PutObjectCommand({
          Bucket: S3_BUCKET,
          Key: file.name,
          Body: file
      });

      try {
          const response = await s3Client.send(command);
          console.log(response);
          alert("File uploaded successfully.");
      } catch (err) {
          console.error(err);
          alert("Failed to upload file.");
      }

    const API_PATH='https://gsnmu8gl3f.execute-api.us-west-1.amazonaws.com/prod/'
    const dynamoDbData = {
      id: nanoid(),
      input_text: inputText,
      input_file_path: `${S3_BUCKET}/${file.name}`,
    };

    const response = await fetch(API_PATH, {
      method: "POST",
      headers: {
        "Content-Type":"application/json",
      },
      body: JSON.stringify(dynamoDbData),
    });
    console.log(response)
    if (!response.ok) {
      throw new Error("Failed to save data to DynamoDB");
    }
    alert("File uploaded and data saved successfully!");

  };

  return (
    <div class="flex justify-center items-center h-screen">
    <div class="max-w-md w-full px-4 py-8 bg-white shadow-lg rounded-lg">
      <div>
        <label for="inputText" class="block text-gray-700">Text input:</label>
        <input
          type="text"
          id="inputText"
          placeholder="Enter text"
          class="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
          value="{inputText}"
          onchange="{handleTextInputChange}"
        />
      </div>
      <div class="mt-4">
        <label for="fileInput" class="block text-gray-700">File input:</label>
        <input
          type="file"
          id="fileInput"
          accept=".txt"
          onchange="{handleFileChange}"
          class="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
        />
      </div>
      <div class="mt-4">
        <button
          type="submit"
          onclick="{uploadFile}"
          class="inline-block bg-indigo-500 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Upload
        </button>
      </div>
    </div>
  </div>

  );
}

export default App;
