import {PutObjectCommand, S3Client} from "@aws-sdk/client-s3";
import { useState } from "react";
import { nanoid } from "nanoid";

function App() {

  const [file, setFile] = useState(null);
  const [inputText, setInputText] = useState('')

  const handleTextInputChange = (event) => {
      setInputText(event.target.value);
      };

const handleFileChange = (e) => {
  const file = e.target.files[0];
  setFile(file);
};

  const uploadFile = async () => {
    const S3_BUCKET = "infracdkstack-projects3bucketf7e4afb6-ljf45fw8yyuw";

    const REGION = "us-west-1";

      const CREDENTIALS = {
        accessKeyId: "AKIARFXZJQECRGPJWSW3",
        secretAccessKey: "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"
      }

      const s3Client = new S3Client({
        region: REGION,
        credentials: CREDENTIALS
    });

      console.log(file)
      const command = new PutObjectCommand({
          Bucket: S3_BUCKET,
          Key: file.name,
          Body: file
      });

      try {
          const response = await s3Client.send(command);
          console.log(response);
          alert("File uploaded successfully.");
      } catch (err) {
          console.error(err);
          alert("Failed to upload file.");
      }

    const API_PATH='https://vi49cp2mkh.execute-api.us-west-1.amazonaws.com/prod'
    const dynamoDbData = {
      id: nanoid(),
      input_text: inputText,
      input_file_path: `${S3_BUCKET}/${file.name}`,
    };

    const response = await fetch(API_PATH, {
      method: "POST",
      headers: {
        "Content-Type":"application/json",
      },
      body: JSON.stringify(dynamoDbData),
    });
    console.log(response)
    if (!response.ok) {
      throw new Error("Failed to save data to DynamoDB");
    }
    alert("File uploaded and data saved successfully!");

  };

  return (
    <div className="App">
      <div>
      <label htmlFor="inputText">Text input:</label>
      <input
        type="text"
        id="inputText"
        placeholder='enter text'
        value={inputText}
        onChange={handleTextInputChange}
      />
      <br />
      <label htmlFor="fileInput">File input:</label>
        <input type="file"
        id="fileInput"
        accept=".txt"
         onChange={handleFileChange} />
         <br />
        <button type="submit" onClick={uploadFile}>Upload</button>
      </div>
    </div>

  );
}

export default App;
